mapper.select:
select member_fee
from gym_member_cards
where user_id = #{userId,jdbcType=BIGINT}

mapper.update:
update gym_member_cards
set member_fee = #{newBalance,jdbcType=DECIMAL}
where user_id = #{userId,jdbcType=BIGINT}

update gym_member_cards
set free_quota_remaining = #{freeQuota,jdbcType=INTEGER}
where user_id = #{userId,jdbcType=BIGINT}
